import styled, { keyframes } from 'styled-components'
import { motion } from 'framer-motion'

export const stagger = {
    initial: {
        opacity: 0,
    },
    animate: {
        opacity: 1,
        transition: {
            default: {
                staggerChildren: 0,
            },
        },
    },
}

export const toggleButton = {
    initial: {
        x: 0,
    },
    animate: {
        x: 15,
    },
    exit: {
        x: 0,
    },
}

export const fadeIn = {
    initial: {
        opacity: 0,
    },
    animate: {
        opacity: 1,
        transition: {
            delay: 1,
        },
    },
}

export const fadeInTop = {
    initial: {
        opacity: 0,
        y: -100,
    },
    animate: {
        opacity: 1,
        y: 0,
        transition: {
            delay: 0.5,
        },
    },
}

export const fadeInLeft = {
    initial: {
        opacity: 0,
        x: 100,
    },
    animate: {
        opacity: 1,
        x: 0,
        transition: {
            duration: 1,
        },
    },
}

export const fadeInRight = {
    initial: {
        opacity: 0,
        x: -100,
    },
    animate: {
        opacity: 1,
        x: 0,
        transition: {
            delay: 1.5,
        },
    },
}

export const exit = {
    initial: {
        opacity: 1,
    },
    animate: {
        opacity: 0,
    },
}

export const headerAnimation = {
    animate: {
        transition: {
            when: 'beforeChildren',
            staggerChildren: 0.5,
        },
    },
}

export const title = {
    initial: {
        opacity: 0,
    },
    animate: {
        opacity: 1,
        transition: {
            when: 'afterChildren',
        },
    },
}

export const navigation = {
    initial: {
        opacity: 0,
        y: -10,
    },
    animate: {
        opacity: 1,
        y: 0,
        transition: {
            default: {
                duration: 1.25,
                when: 'beforeChildren',
            },
        },
    },
}

export const GlobalPadding = styled.div`
    padding: 0rem ${(props) => props.theme.padding};
`

export const YPadding = styled.div`
    padding: ${(props) => props.theme.padding} 0rem;
`

export const XPadding = styled.div`
    padding-left: ${(props) => props.theme.padding};
    padding-right: ${(props) => props.theme.padding};
`

export const XCPadding = styled.div`
    padding-left: ${(props) => props.padding};
    padding-right: ${(props) => props.padding};
`

export const YCPadding = styled.div`
    padding: ${(props) => props.padding} 0rem;
`

export const CPadding = styled.div`
    padding: ${(props) => props.padding};
`

//max-container
export const MaxContainer = styled.div`
    padding: 0rem ${(props) => props.theme.padding};
    max-width: ${(props) => props.theme.maxWidth};
    margin: 0 auto;
`

//mid-container
export const MidContainer = styled.div`
    padding: 0rem ${(props) => props.theme.padding};
    max-width: ${(props) => props.theme.midWidth};
    margin: 0 auto;
`

//small-container
export const SmContainer = styled.div`
    padding: 0rem ${(props) => props.theme.padding};
    max-width: ${(props) => props.theme.smWidth};
    margin: 0 auto;
`

//full-width
export const FullWidth = styled.div`
    width: 100%;
    margin: 0 auto;
`

//flex 50/50 split
export const FlexEven = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    div {
        flex: 1;
    }
`

//flex 1/3 split
export const FlexOneThree = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    @media screen and (max-width: ${(props) => props.theme.midWidth}) {
        display: block;
    }
    div:nth-child(1) {
        flex: 1;
    }
    div:nth-child(2) {
        flex: 2;
    }
`

//flex 3/1 split
export const FlexThreeOne = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    @media screen and (max-width: ${(props) => props.theme.midWidth}) {
        display: block;
    }
    div:nth-child(1) {
        flex: 2;
    }
    div:nth-child(2) {
        flex: 1;
    }
`

//3 column grid
export const GridThree = styled.div`
    display: grid;
    grid-gap: 2rem;
    transition: all 0.25s ease-in-out;
    grid-template-columns: repeat(3, 1fr);
    @media screen and (max-width: ${(props) => props.theme.midWidth}) {
        grid-template-columns: repeat(2, 1fr);
    }
`

//2 column grid
export const GridTwo = styled.div`
    display: grid;
    grid-gap: 2rem;
    transition: all 0.25s ease-in-out;
    grid-template-columns: repeat(2, 1fr);
    @media screen and (max-width: ${(props) => props.theme.smWidth}) {
        display: block;
        div {
            margin: ${(props) => props.theme.padding} 0rem;
        }
    }
`

//main button
export const MainButton = styled(motion.a)`
    padding: 0.5rem 1.5rem;
    text-decoration: none;
    transition: all 0.25s ease-in-out;
    color: ${(props) => props.theme.colors.primary};
    border: 0.3rem solid ${(props) => props.theme.colors.primary};
    display: inline-block;
    margin: 1rem 0rem;
    &:hover {
        background: ${(props) => props.theme.colors.primary};
        text-decoration: none;
        cursor: pointer;
        color: #fff;
    }
`

//flexcenter
export const FlexCenter = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 0 auto;
    height: auto;
`

//flexend
export const FlexEnd = styled.div`
    display: flex;
    justify-content: flex-end;
    align-items: flex-end;
    margin: 0 auto;
    height: 0 auto;
`

//used to describe portfolio pieces
export const Meta = styled(motion.div)`
    display: flex;
    align-items: flex-start;
    max-width: 80rem;
    margin: 0 auto;
    div {
        padding-right: ${(props) => props.theme.padding};
        &:nth-child(1) {
            flex: 1.2;
        }
        &:nth-child(2) {
            flex: 1.2;
        }
        &:nth-child(3) {
            flex-basis: auto;
            min-width: 9rem;
            max-width: 10rem;
            padding-right: 0;
        }
    }
    ul {
        li {
            display: inline;
