//acts as a theme switcher + global variables
export const darkMode = {
	fonts: {
		main: 'DM Sans', //fonts located in header file
		header: 'Helvetica Black',
		reading: '2rem',
	},
	padding: '2.5rem',
	maxWidth: '1440px',
	midWidth: '768px',
	smWidth: '640px',
	colors: {
		primary: '#53B21F',
		secondary: '#47991A',
		block: '#444',
		icon: '#80F342',
		text: '#fff',
		grey: '#666',
		background: '#333',
		navBackground: '#222',
		success: 'green',
		disabled: '#888',
		danger: '#cf2317',
		warning: 'yellow',
		info: 'teal',
		light: '#fff',
		dark: '#000',
	},
};

export default darkMode;
